	<?php  	// We'll be granting access to only a single domain to make this script safe 	// USAGE 	// https://<phpserver>/get.php?u=remote url without protocol 	// if destination url is http based use https://<phpserver>/get.php?ssl=http&u=remote url without protocol 	// if the destination url is an image file use https://<phpserver>/get.php?i=jpg&u=remote url without protocol 	// Image Type can be changed to jpg, png, gif or any supported image mime type^   	$allowCORS = 'https://www.success-trending.club'; 	$whitelist = array( 	"www.google.com", 	"unsplash.com");   	function customError($errno, $errstr) {   	echo "<html><head><title>ERROR</title>";	echo "</head><body><b>Error:</b> [$errno] <br>";	echo "<p>Unknown Protocol</p><br></body></html>";  	die();	} 	if(empty($_GET['ssl'])){	$u = "https://".$_GET['u']; 	} elseif (($_GET['ssl']) == 'https') {	$u = "https://".$_GET['u']; 	} elseif (($_GET['ssl']) == 'http') {	$u = "http://".$_GET['u']; 	} else {	$u = "https://".$_GET['u']; 	} 	$domainaddr = parse_url($u, PHP_URL_HOST);	$whitelisted = 0;	$count = count($whitelist);	for ($i = 0; $i < $count; $i++) {	if($domainaddr == $whitelist[$i]) {	$whitelisted = 1;	break;	}	} 	set_error_handler("customError");	if($whitelisted == 1) {	$content = file_get_contents($u); 	header('Access-Control-Allow-Origin: ' . $allowCORS);	if(isset($_GET['i'])) {	header('Content-Type: image/' . $_GET['i']);	}	echo $content;	} else {    	header('Content-Type: text/html');	echo "<html>";	echo "<head>";	echo "<title>Another Resource</title>";	echo "</head>";	echo "<body>";	echo "<h1> 	Access Denied </h1>	";	echo "<p>	Hostname: " . $domainaddr . "</p>	";	echo "<p>	URL: " . $u . "</p>	";	echo "</body>";	echo "</html>"; 	} 	?>
